settings.custom_liquid:
{% if product.vendor contains ' e ' %}
  {% assign authors = product.vendor | split: ' e ' %} 
  {% assign author = authors[0] %}
  {% assign coauthor = authors[1] %}
{% else %}
  {% assign author = product.vendor %}
{% endif %}

{% assign illustrator = product.metafields.autor.ilustrador %}
{% assign editor = product.metafields.colaborador.editor %}
{% assign inspired = product.metafields.infotecnica.inspirado %}

{% assign authorurl = author | handleize %}
{% assign coauthorurl = coauthor | handleize %}
{% assign illustratorurl = illustrator | handleize %}
{% assign inspiredourl = inspired | handleize %}
{% assign dados = product.metafields.infotecnica.dados %}

{% unless author == "§" or author == "Solisluna Editora" %}
<a href="#{{ authorurl }}" class="author"><span class="h3" itemprop="author">{{ author }}</span></a>{% if coauthor %}<span class="h3 author"> e </span><a href="#{{ coauthorurl }}"class="author" ><span class="h3 " itemprop="author">{{ coauthor }}</span></a>{% endif %}
          {% endunless %}

{% if section.settings.product_illustrator_enable and illustrator %}
            <div class="h4 author illustrator" itemprop="illustrator">Ilustrado por <a href="#{{ illustratorurl }}">{{ illustrator }}</a></div>
          {% endif %}
          {% if section.settings.product_colaborador_enable and editor %}
            <span class="h4 author illustrator" itemprop="editor">Criado e editado por {{ editor }}</span>
          {% endif %}
